mapper.select:
select count(*)
from eventzone
where business_type = #{business_type}

mapper.insert:
insert into eventzone(
no
,content
,subject
,writer
,location
,recommand
,dates
,business_nm
,business_type
,hit
,secret
) values (
(select ifnull(max(no),0)+1 from eventzone asdf)
,#{content}
, #{subject}
, #{writer}
, #{location}
, '0'
, now()
, #{business_nm}
, #{business_type}
,'0'
,#{secret}
);

mapper.update:
UPDATE eventzone SET
	   content = #{content}
	, subject = #{subject}
	, writer = #{writer}
	, location = #{location}
	, business_nm = #{business_nm}
	, business_type = #{business_type}
	, secret = #{secret}
	WHERE no = #{no}
