mapper.select:
select * from (select @rn:=@rn+1 as rownum, after.* from (select no , content , subject , writer , location , recommand , date_format(dates,'%m-%d') dates , business_nm , business_type , hit , secret from useafter where 1=1
and business_type = #{criteria.business_type}
and location = #{criteria.location}
order by no desc ) as after, (select @rn:=0) as R ) aftertable
where rownum between #{criteria.startRow} and #{criteria.endRow}

select count(*)
from useafter
where 
1=1

and business_type = #{criteria.business_type}

	
	   	and location = #{criteria.location}

mapper.insert:
insert into useafter(
no
,content
,subject
,writer
,location
,recommand
,dates
,business_nm
,business_type
,hit
,secret
) values (
(select ifnull(max(no),0)+1 from useafter asdf)
,#{content}
, #{subject}
, #{writer}
, #{location}
, '0'
, now()
, #{business_nm}
, #{business_type}
,'0'
,#{secret}
);

mapper.select:
select * from (select @rn:=@rn+1 as rownum, use_reply.* from (select rno , no , replace(replace(replytext,'\r\n',''),'\r','') replytext , replyer , date_format(regdate,'%Y-%m-%d %h:%m') regdate , date_format(updatedate, '%Y-%m-%d %h:%m') updatedate
from useafter_reply where no = #{useafter_DTO.no}) use_reply , (select @rn:=0) as R) after_reply
where rownum between #{criteria.startRow} and #{criteria.endRow};

select count(*) 
from useafter_reply
where no = #{useafter_DTO.no}

mapper.update:
UPDATE useafter SET
	  content = #{content}
	, subject = #{subject}
	, writer = #{writer}
	, location = #{location}
	, business_nm = #{business_nm}
	, business_type = #{business_type}
	, secret = #{secret}
	WHERE no = #{no}

mapper.select:
select count(*) 
from recommand
where
userId = #{userId}
and b_no = #{useafter_DTO.no}
and table_nm = #{table_nm}
and business_type = #{useafter_DTO.business_type}

mapper.insert:
insert into recommand(
userId
, b_no
, table_nm
, business_type

)values(
#{userId}
, #{useafter_DTO.no}
, #{table_nm}
, #{useafter_DTO.business_type}

)

mapper.update:
update useafter SET
recommand = (select * from 
						(select ifnull(max(recommand),0)+1 
						from useafter asd 
						where no=#{no} 
						and business_type=#{business_type}
						) asd )
where
no =#{no} 
and business_type = #{business_type}
