mapper.select:
select * from (select @rn:=@rn+1) as rownum), use_reply.* (select rno , no , replytext , replyer , date_format(regdate,%Y-%m-%d) regdate , date_format(updatedate, %Y-%m-%d) updatedate
from useafter_reply where no = #{reply_DTO.no}) use_reply , (select @rn:=0) as R) after_reply
where rownum between #{criteria.startRow} and #{criteria.endRow}

select count(*)
from useafter_reply
where no = #{no}

mapper.update:
update  useafter_reply set
replytext = #{replytext}
where
rno = #{rno}
